import React, { useState, useEffect, useRef } from 'react';

export function ImageChanger() {
  const [imageUrl, setImageUrl] = useState('');
  const fileInputRef = useRef(null);

  useEffect(() => {
    // Load initial image on mount
    loadImage();
  }, []);

  const loadImage = () => {
    // Load image from URL or local file
    // Here we are just using a placeholder image URL
    setImageUrl('https://wallpaper-mania.com/wp-content/uploads/2018/09/High_resolution_wallpaper_background_ID_77701385699.jpg');
  };

  const handleButtonClick = () => {
    // Open file picker to select a new image
    fileInputRef.current.click();
  };

  const handleFileChange = (event) => {
    // Update the image URL with the new file
    const file = event.target.files[0];
    const imageUrl = URL.createObjectURL(file);
    setImageUrl(imageUrl);
  };

  return (
    <div>
      <img src={imageUrl}  />
      <input type="file" ref={fileInputRef} onChange={handleFileChange} />
      <button onClick={handleButtonClick}>Change Image</button>
    </div>
  );
}
